import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { updatePost, setCurrentPost } from '../store/slices/postsSlice';
import { addNotification } from '../store/slices/uiSlice';
import apiService from '../services/api';
import { 
  Save, 
  Eye, 
  Image, 
  Tag, 
  Type, 
  FileText,
  X
} from 'lucide-react';
import './CreateEditPost.css';

const EditPost = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const { currentPost } = useSelector(state => state.posts);
  const [formData, setFormData] = useState({
    title: '',
    content: '',
    excerpt: '',
    tags: '',
    image: '',
  });
  const [errors, setErrors] = useState({});
  const [isPreview, setIsPreview] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    const load = async () => {
      try {
        const res = await apiService.getPost(id);
        const p = res?.data?.post;
        if (p) {
          const normalized = {
            id: p._id,
            title: p.title,
            content: p.content,
            excerpt: p.excerpt,
            tags: p.tags || [],
            image: p.image,
          };
          dispatch(setCurrentPost(normalized));
          setFormData({
            title: normalized.title,
            content: normalized.content,
            excerpt: normalized.excerpt,
            tags: normalized.tags.join(', '),
            image: normalized.image,
          });
        }
      } catch {}
    };
    load();
  }, [id, dispatch]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: '',
      }));
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setFormData(prev => ({
          ...prev,
          image: e.target.result,
        }));
      };
      reader.readAsDataURL(file);
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.title.trim()) {
      newErrors.title = 'Title is required';
    } else if (formData.title.trim().length < 5) {
      newErrors.title = 'Title must be at least 5 characters';
    }

    if (!formData.content.trim()) {
      newErrors.content = 'Content is required';
    } else if (formData.content.trim().length < 50) {
      newErrors.content = 'Content must be at least 50 characters';
    }

    if (!formData.excerpt.trim()) {
      newErrors.excerpt = 'Excerpt is required';
    } else if (formData.excerpt.trim().length < 20) {
      newErrors.excerpt = 'Excerpt must be at least 20 characters';
    }

    if (!formData.tags.trim()) {
      newErrors.tags = 'At least one tag is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) return;

    setIsSubmitting(true);

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));

      const updatedPost = {
        ...currentPost,
        title: formData.title.trim(),
        content: formData.content.trim(),
        excerpt: formData.excerpt.trim(),
        tags: formData.tags.split(',').map(tag => tag.trim()).filter(tag => tag),
        image: formData.image,
        updatedAt: new Date().toISOString(),
        readTime: `${Math.ceil(formData.content.split(' ').length / 200)} min read`,
      };

      dispatch(updatePost(updatedPost));

      dispatch(addNotification({
        type: 'success',
        title: 'Post Updated!',
        message: 'Your post has been updated successfully.',
      }));

      navigate(`/post/${updatedPost.id}`);
    } catch (error) {
      dispatch(addNotification({
        type: 'error',
        title: 'Error',
        message: 'Failed to update post. Please try again.',
      }));
    } finally {
      setIsSubmitting(false);
    }
  };

  const handlePreview = () => {
    setIsPreview(!isPreview);
  };

  const wordCount = formData.content.split(' ').length;
  const readTime = Math.ceil(wordCount / 200);

  if (!currentPost) {
    return (
      <div className="create-post-page">
        <div className="container">
          <div className="post-editor">
            <div className="loading-state">
              <h2>Loading post...</h2>
              <p>Please wait while we load the post for editing.</p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="create-post-page">
      <div className="container">
        <div className="post-editor">
          <div className="editor-header">
            <h1 className="editor-title">Edit Post</h1>
            <div className="editor-actions">
              <button
                type="button"
                className="btn btn-secondary"
                onClick={handlePreview}
              >
                <Eye size={16} />
                {isPreview ? 'Edit' : 'Preview'}
              </button>
              <button
                type="submit"
                form="post-form"
                className="btn btn-primary"
                disabled={isSubmitting}
              >
                <Save size={16} />
                {isSubmitting ? 'Updating...' : 'Update Post'}
              </button>
            </div>
          </div>

          <form id="post-form" onSubmit={handleSubmit} className="editor-form">
            <div className="editor-content">
              {!isPreview ? (
                <div className="editor-inputs">
                  <div className="form-group">
                    <label htmlFor="title" className="form-label">
                      <Type size={16} />
                      Post Title
                    </label>
                    <input
                      type="text"
                      id="title"
                      name="title"
                      value={formData.title}
                      onChange={handleChange}
                      className={`form-input ${errors.title ? 'error' : ''}`}
                      placeholder="Enter your post title..."
                    />
                    {errors.title && <span className="error-message">{errors.title}</span>}
                  </div>

                  <div className="form-group">
                    <label htmlFor="excerpt" className="form-label">
                      <FileText size={16} />
                      Excerpt
                    </label>
                    <textarea
                      id="excerpt"
                      name="excerpt"
                      value={formData.excerpt}
                      onChange={handleChange}
                      className={`form-input form-textarea ${errors.excerpt ? 'error' : ''}`}
                      placeholder="Write a brief description of your post..."
                      rows="3"
                    />
                    {errors.excerpt && <span className="error-message">{errors.excerpt}</span>}
                  </div>

                  <div className="form-group">
                    <label htmlFor="content" className="form-label">
                      <FileText size={16} />
                      Content
                    </label>
                    <textarea
                      id="content"
                      name="content"
                      value={formData.content}
                      onChange={handleChange}
                      className={`form-input form-textarea ${errors.content ? 'error' : ''}`}
                      placeholder="Write your post content here..."
                      rows="15"
                    />
                    <div className="content-stats">
                      <span>{wordCount} words</span>
                      <span>{readTime} min read</span>
                    </div>
                    {errors.content && <span className="error-message">{errors.content}</span>}
                  </div>

                  <div className="form-group">
                    <label htmlFor="tags" className="form-label">
                      <Tag size={16} />
                      Tags
                    </label>
                    <input
                      type="text"
                      id="tags"
                      name="tags"
                      value={formData.tags}
                      onChange={handleChange}
                      className={`form-input ${errors.tags ? 'error' : ''}`}
                      placeholder="Enter tags separated by commas (e.g., React, JavaScript, Tutorial)"
                    />
                    {errors.tags && <span className="error-message">{errors.tags}</span>}
                  </div>

                  <div className="form-group">
                    <label htmlFor="image" className="form-label">
                      <Image size={16} />
                      Featured Image
                    </label>
                    <div className="image-upload">
                      <input
                        type="file"
                        id="image"
                        accept="image/*"
                        onChange={handleImageChange}
                        className="image-input"
                      />
                      <label htmlFor="image" className="image-upload-label">
                        <Image size={20} />
                        Choose Image
                      </label>
                      <div className="image-preview">
                        <img src={formData.image} alt="Preview" />
                        <button
                          type="button"
                          onClick={() => setFormData(prev => ({ ...prev, image: '' }))}
                          className="remove-image"
                        >
                          <X size={16} />
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              ) : (
                <div className="post-preview">
                  <div className="preview-image">
                    <img src={formData.image} alt={formData.title} />
                  </div>
                  <div className="preview-content">
                    <h1 className="preview-title">{formData.title || 'Untitled'}</h1>
                    <p className="preview-excerpt">{formData.excerpt || 'No excerpt provided'}</p>
                    <div className="preview-tags">
                      {formData.tags.split(',').map((tag, index) => (
                        <span key={index} className="preview-tag">
                          #{tag.trim()}
                        </span>
                      ))}
                    </div>
                    <div className="preview-text">
                      {formData.content.split('\n\n').map((paragraph, index) => (
                        <p key={index}>{paragraph}</p>
                      ))}
                    </div>
                  </div>
                </div>
              )}
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default EditPost;
