import React from 'react';
import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { toggleLike, toggleBookmark } from '../../store/slices/postsSlice';
import { addNotification } from '../../store/slices/uiSlice';
import { Heart, Bookmark, Eye, MessageCircle, Calendar } from 'lucide-react';
import { format } from 'date-fns';
import apiService from '../../services/api';
import './PostCard.css';

const PostCard = ({ post }) => {
  const dispatch = useDispatch();
  const { user, isAuthenticated } = useSelector(state => state.auth);
  const isLiked = user ? post.likes.some(like => like.toString() === user._id) : false;
  const isBookmarked = user ? post.bookmarks.some(bookmark => bookmark.toString() === user._id) : false;

  const handleLike = async (e) => {
    e.preventDefault();
    if (!isAuthenticated) {
      dispatch(addNotification({
        type: 'warning',
        title: 'Login Required',
        message: 'Please login to like posts'
      }));
      return;
    }

    try {
      const response = await apiService.likePost(post._id);
      dispatch(toggleLike({ postId: post._id, userId: user._id }));
      dispatch(addNotification({
        type: 'success',
        title: response.data.isLiked ? 'Post Liked!' : 'Post Unliked!',
        message: response.message
      }));
    } catch (error) {
      dispatch(addNotification({
        type: 'error',
        title: 'Error',
        message: 'Failed to like post. Please try again.'
      }));
    }
  };

  const handleBookmark = async (e) => {
    e.preventDefault();
    if (!isAuthenticated) {
      dispatch(addNotification({
        type: 'warning',
        title: 'Login Required',
        message: 'Please login to bookmark posts'
      }));
      return;
    }

    try {
      const response = await apiService.bookmarkPost(post._id);
      dispatch(toggleBookmark({ postId: post._id, userId: user._id }));
      dispatch(addNotification({
        type: 'success',
        title: response.data.isBookmarked ? 'Post Bookmarked!' : 'Post Unbookmarked!',
        message: response.message
      }));
    } catch (error) {
      dispatch(addNotification({
        type: 'error',
        title: 'Error',
        message: 'Failed to bookmark post. Please try again.'
      }));
    }
  };

  return (
    <article className="post-card">
      <Link to={`/post/${post.id}`} className="post-link">
        <div className="post-image-container">
          <img
            src={post.image}
            alt={post.title}
            className="post-image"
            loading="lazy"
          />
          <div className="post-overlay">
            <div className="post-tags">
              {post.tags.slice(0, 3).map(tag => (
                <span key={tag} className="post-tag">
                  {tag}
                </span>
              ))}
            </div>
          </div>
        </div>

        <div className="post-content">
          <div className="post-header">
            <h2 className="post-title">{post.title}</h2>
            <p className="post-excerpt">{post.excerpt}</p>
          </div>

          <div className="post-meta">
            <div className="post-author">
              <img
                src={post.author.avatar}
                alt={post.author.name}
                className="author-avatar"
              />
              <div className="author-info">
                <span className="author-name">{post.author.name}</span>
                <span className="post-date">
                  <Calendar size={14} />
                  {format(new Date(post.createdAt), 'MMM d, yyyy')}
                </span>
              </div>
            </div>

            <div className="post-stats">
              <span className="post-stat">
                <Eye size={16} />
                {post.views}
              </span>
              <span className="post-stat">
                <MessageCircle size={16} />
                {post.comments}
              </span>
              <span className="post-read-time">{post.readTime}</span>
            </div>
          </div>
        </div>
      </Link>

      <div className="post-actions">
        <button
          className={`action-btn ${isLiked ? 'liked' : ''}`}
          onClick={handleLike}
          disabled={!isAuthenticated}
          title={isLiked ? 'Unlike' : 'Like'}
        >
          <Heart size={18} />
          <span>{post.likes.length}</span>
        </button>

        <button
          className={`action-btn ${isBookmarked ? 'bookmarked' : ''}`}
          onClick={handleBookmark}
          disabled={!isAuthenticated}
          title={isBookmarked ? 'Remove bookmark' : 'Bookmark'}
        >
          <Bookmark size={18} />
        </button>
      </div>
    </article>
  );
};

export default PostCard;
