import React from 'react';
import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { toggleLike, toggleBookmark } from '../../store/slices/postsSlice';
import apiService from '../../services/api';
import { Heart, Bookmark, Eye, MessageCircle, Calendar } from 'lucide-react';
import { format } from 'date-fns';
import './PostCard.css';

const PostCard = ({ post }) => {
  const dispatch = useDispatch();
  const { user, isAuthenticated } = useSelector(state => state.auth);
  const isLiked = user ? post.likes.includes(user.id) : false;
  const isBookmarked = user ? post.bookmarks.includes(user.id) : false;
  const isAuthor = user && (post.author?.id === user.id);

  const handleLike = async (e) => {
    e.preventDefault();
    if (!isAuthenticated) return;
    dispatch(toggleLike({ postId: post.id, userId: user.id }));
    try {
      await apiService.likePost(post.id);
    } catch (err) {
      // no-op for now; optimistic update
    }
  };

  const handleBookmark = async (e) => {
    e.preventDefault();
    if (!isAuthenticated) return;
    dispatch(toggleBookmark({ postId: post.id, userId: user.id }));
    try {
      await apiService.bookmarkPost(post.id);
    } catch (err) {
      // ignore
    }
  };

  const handleDelete = async (e) => {
    e.preventDefault();
    if (!isAuthor) return;
    // optimistic remove from store
    try {
      // backend delete
      const { default: apiService } = await import('../../services/api');
      await apiService.deletePost(post.id);
      // remove locally via a custom event the pages listen to, or rely on backend reload
      // For now, dispatch delete directly
      const { deletePost } = await import('../../store/slices/postsSlice');
      dispatch(deletePost(post.id));
    } catch (err) {
      // ignore for now
    }
  };

  return (
    <article className="post-card">
      <Link to={`/post/${post.id}`} className="post-link">
        <div className="post-image-container">
          <img
            src={post.image}
            alt={post.title}
            className="post-image"
            loading="lazy"
          />
        </div>

        <div className="post-content">
          <div className="post-header">
            <h2 className="post-title">{post.title}</h2>
            <p className="post-excerpt">{post.excerpt}</p>
          </div>

          <div className="post-meta">
            <div className="post-author">
              <img
                src={post.author.avatar}
                alt={post.author.name}
                className="author-avatar"
              />
              <div className="author-info">
                <span className="author-name">{post.author.name}</span>
                <span className="post-date">
                  <Calendar size={14} />
                  {format(new Date(post.createdAt), 'MMM d, yyyy')}
                </span>
              </div>
            </div>

            <div className="post-stats">
              <span className="post-stat">
                <Eye size={16} />
                {post.views}
              </span>
              <span className="post-stat">
                <MessageCircle size={16} />
                {post.comments}
              </span>
              <span className="post-read-time">{post.readTime}</span>
            </div>
          </div>
        </div>
      </Link>


    </article>
  );
};

export default PostCard;
