import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { setCurrentPost, toggleLike, toggleBookmark } from '../store/slices/postsSlice';
import { dummyPosts } from '../data/dummyData';
import { format } from 'date-fns';
import { 
  Heart, 
  Bookmark, 
  Eye, 
  MessageCircle, 
  Calendar, 
  Clock, 
  Share2, 
  Edit, 
  Trash2,
  ArrowLeft,
  User
} from 'lucide-react';
import LoadingSpinner from '../components/UI/LoadingSpinner';
import './PostDetail.css';

const PostDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const { currentPost, posts } = useSelector(state => state.posts);
  const { user, isAuthenticated } = useSelector(state => state.auth);
  const [isLiked, setIsLiked] = useState(false);
  const [isBookmarked, setIsBookmarked] = useState(false);

  useEffect(() => {
    // Find post in dummy data
    const post = posts.find(p => p.id === id) || dummyPosts.find(p => p.id === id);
    if (post) {
      dispatch(setCurrentPost(post));
      setIsLiked(user ? post.likes.includes(user.id) : false);
      setIsBookmarked(user ? post.bookmarks.includes(user.id) : false);
    }
  }, [id, posts, user, dispatch]);

  const handleLike = () => {
    if (isAuthenticated && currentPost) {
      dispatch(toggleLike({ postId: currentPost.id, userId: user.id }));
      setIsLiked(!isLiked);
    }
  };

  const handleBookmark = () => {
    if (isAuthenticated && currentPost) {
      dispatch(toggleBookmark({ postId: currentPost.id, userId: user.id }));
      setIsBookmarked(!isBookmarked);
    }
  };

  const handleEdit = () => {
    navigate(`/edit-post/${currentPost.id}`);
  };

  const handleDelete = () => {
    if (window.confirm('Are you sure you want to delete this post?')) {
      // In a real app, this would dispatch a delete action
      navigate('/');
    }
  };

  const handleShare = async () => {
    if (navigator.share) {
      try {
        await navigator.share({
          title: currentPost.title,
          text: currentPost.excerpt,
          url: window.location.href,
        });
      } catch (err) {
        console.log('Error sharing:', err);
      }
    } else {
      // Fallback to clipboard
      navigator.clipboard.writeText(window.location.href);
    }
  };

  if (!currentPost) {
    return <LoadingSpinner text="Loading post..." />;
  }

  const isAuthor = user && currentPost.author.id === user.id;

  return (
    <div className="post-detail">
      <div className="container">
        <button 
          className="back-button"
          onClick={() => navigate(-1)}
        >
          <ArrowLeft size={20} />
          Back
        </button>

        <article className="post-article">
          <header className="post-header">
            <div className="post-meta">
              <div className="post-dates">
                <span className="post-date">
                  <Calendar size={16} />
                  {format(new Date(currentPost.createdAt), 'MMM d, yyyy')}
                </span>
                <span className="post-read-time">
                  <Clock size={16} />
                  {currentPost.readTime}
                </span>
              </div>

              <div className="post-actions">
                <button
                  className={`action-btn ${isLiked ? 'liked' : ''}`}
                  onClick={handleLike}
                  disabled={!isAuthenticated}
                  title={isLiked ? 'Unlike' : 'Like'}
                >
                  <Heart size={20} />
                  <span>{currentPost.likes.length}</span>
                </button>

                <button
                  className={`action-btn ${isBookmarked ? 'bookmarked' : ''}`}
                  onClick={handleBookmark}
                  disabled={!isAuthenticated}
                  title={isBookmarked ? 'Remove bookmark' : 'Bookmark'}
                >
                  <Bookmark size={20} />
                </button>

                <button
                  className="action-btn"
                  onClick={handleShare}
                  title="Share"
                >
                  <Share2 size={20} />
                </button>

                {isAuthor && (
                  <>
                    <button
                      className="action-btn"
                      onClick={handleEdit}
                      title="Edit post"
                    >
                      <Edit size={20} />
                    </button>
                    <button
                      className="action-btn delete"
                      onClick={handleDelete}
                      title="Delete post"
                    >
                      <Trash2 size={20} />
                    </button>
                  </>
                )}
              </div>
            </div>

            <h1 className="post-title">{currentPost.title}</h1>
            <p className="post-excerpt">{currentPost.excerpt}</p>

            <div className="post-tags">
              {currentPost.tags.map(tag => (
                <span key={tag} className="post-tag">
                  #{tag}
                </span>
              ))}
            </div>
          </header>

          <div className="post-image-container">
            <img
              src={currentPost.image}
              alt={currentPost.title}
              className="post-image"
            />
          </div>

          <div className="post-content">
            {currentPost.content.split('\n\n').map((paragraph, index) => (
              <p key={index} className="post-paragraph">
                {paragraph}
              </p>
            ))}
          </div>

          <footer className="post-footer">
            <div className="post-stats">
              <div className="stat">
                <Eye size={20} />
                <span>{currentPost.views} views</span>
              </div>
              <div className="stat">
                <Heart size={20} />
                <span>{currentPost.likes.length} likes</span>
              </div>
              <div className="stat">
                <MessageCircle size={20} />
                <span>{currentPost.comments} comments</span>
              </div>
            </div>

            <div className="author-card">
              <img
                src={currentPost.author.avatar}
                alt={currentPost.author.name}
                className="author-avatar-large"
              />
              <div className="author-info-large">
                <h3 className="author-name-large">{currentPost.author.name}</h3>
                <p className="author-bio">
                  Passionate writer and tech enthusiast. Love sharing knowledge through blogging.
                </p>
                <button className="btn btn-secondary btn-sm">
                  <User size={16} />
                  Follow
                </button>
              </div>
            </div>
          </footer>
        </article>
      </div>
    </div>
  );
};

export default PostDetail;
