import React from 'react';
import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { toggleLike, toggleBookmark } from '../../store/slices/postsSlice';
import apiService from '../../services/api';
import { Heart, Bookmark, Eye, MessageCircle, Calendar, Edit, Trash2 } from 'lucide-react';
import { format } from 'date-fns';
import './PostCard.css';

const PostCard = ({ post, variant = 'default' }) => {
  const dispatch = useDispatch();
  const { user, isAuthenticated } = useSelector(state => state.auth);
  const isLiked = user ? post.likes.includes(user.id) : false;
  const isBookmarked = user ? post.bookmarks.includes(user.id) : false;
  const isAuthor = user && (post.author?.id === user.id);

  const handleLike = async (e) => {
    e.preventDefault();
    if (!isAuthenticated) return;
    dispatch(toggleLike({ postId: post.id, userId: user.id }));
    try {
      await apiService.likePost(post.id);
    } catch (err) {
      // no-op for now; optimistic update
    }
  };

  const handleBookmark = async (e) => {
    e.preventDefault();
    if (!isAuthenticated) return;
    dispatch(toggleBookmark({ postId: post.id, userId: user.id }));
    try {
      await apiService.bookmarkPost(post.id);
    } catch (err) {
      // ignore
    }
  };

  const handleDelete = async (e) => {
    e.preventDefault();
    if (!isAuthor) return;
    // optimistic remove from store
    try {
      // backend delete
      const { default: apiService } = await import('../../services/api');
      await apiService.deletePost(post.id);
      // remove locally via a custom event the pages listen to, or rely on backend reload
      // For now, dispatch delete directly
      const { deletePost } = await import('../../store/slices/postsSlice');
      dispatch(deletePost(post.id));
    } catch (err) {
      // ignore for now
    }
  };

  return (
    <article className={`post-card ${variant === 'home' ? 'home-card modern' : ''}`}>
      <Link to={`/post/${post.id}`} className="post-link">
        <div className="post-image-container">
          <img
            src={post.image}
            alt={post.title}
            className="post-image"
            loading="lazy"
          />
          <div className="card-badge-group">
            {post.tags.slice(0, 2).map(tag => (
              <span key={tag} className="badge">#{tag}</span>
            ))}
          </div>
          <div className="gradient-overlay" />
        </div>

        <div className="post-content">
          <h2 className="post-title">{post.title}</h2>
          <p className="post-excerpt">{post.excerpt}</p>

          <div className="post-meta">
            <div className="post-author">
              <img
                src={post.author.avatar}
                alt={post.author.name}
                className="author-avatar"
              />
              <div className="author-info">
                <span className="author-name">{post.author.name}</span>
                <span className="post-date">
                  <Calendar size={14} />
                  {format(new Date(post.createdAt), 'MMM d, yyyy')}
                </span>
              </div>
            </div>
            <span className="post-read-time">{post.readTime}</span>
          </div>
        </div>
      </Link>

      <div className="card-footer">
        <div className="stats-left">
          <span className="post-stat"><Eye size={16} />{post.views}</span>
          <span className="post-stat"><MessageCircle size={16} />{post.comments}</span>
        </div>
        <div className="actions-right">
          {isAuthor && (
            <>
              <Link to={`/edit-post/${post.id}`} className="icon-btn" title="Edit">
                <Edit size={18} />
              </Link>
              <button className="icon-btn danger" onClick={handleDelete} title="Delete">
                <Trash2 size={18} />
              </button>
            </>
          )}
          <button
            className={`icon-btn ${isLiked ? 'liked' : ''}`}
            onClick={handleLike}
            disabled={!isAuthenticated}
            title={isLiked ? 'Unlike' : 'Like'}
          >
            <Heart size={18} />
            <span>{post.likes.length}</span>
          </button>
          <button
            className={`icon-btn ${isBookmarked ? 'bookmarked' : ''}`}
            onClick={handleBookmark}
            disabled={!isAuthenticated}
            title={isBookmarked ? 'Remove bookmark' : 'Bookmark'}
          >
            <Bookmark size={18} />
          </button>
        </div>
      </div>
    </article>
  );
};

export default PostCard;
